DROP TABLE IF EXISTS `token_log`;
CREATE TABLE `token_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `ctime` int(11) DEFAULT '0' COMMENT '创建时间',
  `utime` int(11) DEFAULT '0' COMMENT '最后一次获取token时间',
  `access_token` varchar(1000) DEFAULT NULL COMMENT '获取token值',
  `expires_in` int(11) DEFAULT '0' COMMENT '有效时间',
  `appid` int(11) DEFAULT NULL COMMENT '飞印应用appid',
  `appkey` varchar(50) DEFAULT NULL COMMENT '秘钥',
  `MEMBERCODE` varchar(500) DEFAULT NULL COMMENT '应用唯一标识',
  `device_no` varchar(255) DEFAULT NULL COMMENT '设备号',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=latin1 COMMENT='应用账号配置';

